mapper.insert:
INSERT INTO CO_USER(
	USER_ID
	, USER_PWD
	, NAME
	, POST
	, ADDR1
	, ADDR2
	, SNS_DIV
	, SNS_CD
) VALUES (
	#{userId}
	, #{usePwd}
	, #{name}
	, #{post}
	, #{addr1}
	, #{addr2}
	, #{snsDiv}
	, #{snsCd}
)

mapper.update:
UPDATE CO_USER SET
	MODI_DT = NOW()

		, USER_PWD = #{userPwd}


		, NAME = #{name}


		, POST = #{post}


		, ADDR1 = #{addr1}


		, ADDR2 = #{addr2}


		, USE_YN = #{useYn}

WHERE USER_ID = #{userId}
